mapper.update:

        UPDATE tab_goods SET status = 1
        WHERE
	        id = #{id}

sql.where:


AND A.id = #{id}


AND A.materialNum  LIKE CONCAT(CONCAT('%', #{materialNum}), '%')


AND A.goodsName LIKE CONCAT(CONCAT('%', #{goodsName}), '%')


AND A.unitPrice = #{unitPrice}


AND A.unit = #{unit}


AND A.spec = #{spec}


AND A.specUnit = #{specUnit}


AND B.customerName LIKE CONCAT(CONCAT('%', #{customerName}), '%')


AND A.storage = #{storage}


AND A.status = #{status}


AND D.business LIKE CONCAT(CONCAT('%', #{business}), '%')


AND A.memo = #{memo}

AND A.status=0

mapper.select:
SELECT A.id, A.materialNum, A.goodsName, A.unitPrice, A.unit, G.dictName name1, A.spec, A.specUnit, E.dictName name2, A.customerId, B.customerName, A.storage, A.businessId, D.business, A.memo FROM tab_goods A left join tab_customer B on A.customerId=B.id left join tab_business D on A.businessId=D.id left join tab_dict G on A.unit=G.dictNo left join tab_dict E on A.specUnit=E.dictNo left join tab_dm F on F.bm=E.dmbm
ORDER BY ${sortColumns}
LIMIT #{startNum}, #{pageSize}

SELECT id,materialNum
FROM tab_good_customer
WHERE  goodId = #{goodId}
