mapper.update:

        UPDATE tab_orderitem SET
	        customerGoodId = #{customerGoodId} ,
	        esgouNum = #{esgouNum} ,
	        unitPrice = #{unitPrice}
        WHERE
	        id = #{id}

sql.where:
A.orderId =  #{orderId}

    AND A.id = #{id}


    AND A.customerGoodId = #{customerGoodId}


    AND A.unitPrice = #{unitPrice}


    AND A.esgouNum = #{esgouNum}

mapper.select:
SELECT A.id, A.orderId, B.orderName, A.customerGoodId, D.goodsName, F.materialNum, A.esgouNum, A.unitPrice, D.specUnit, E.dictName, A.esgouNum*A.unitPrice as totalMoney FROM tab_orderitem A left join tab_order B on A.orderId=B.id left join tab_good_customer F on A.customerGoodId=F.id left join tab_goods D on F.goodId=D.id left join tab_dict E on D.specUnit=E.dictNo
ORDER BY ${sortColumns}
LIMIT #{startNum}, #{pageSize}
